import Image from 'next/image';
import React from 'react'
import { Col } from 'react-bootstrap';

function ListaImagenes({imagenes, setImagen, imagen, nombreProducto}) {
  return (
    <>
    {imagenes?.map((imagen_) => {
      return (
        <Col
          key={imagen_.id}
          xs={4}
          sm={4}
          md={4}
          lg={3}
          xl={3}
          xxl={3}
        >
          <Image
            className={
              imagen == imagen_.index
                ? "foto-producto seleccionado rounded img-fluid imagen-noarrastrable"
                : "foto-producto rounded img-fluid imagen-noarrastrable"
            }
            onClick={() => {
              setImagen(imagen_.index);
            }}
            src={imagen_.url}
            height="150"
            width="150"
            quality={30}
            placeholder="blur"
            blurDataURL="data:image/jpeg;base64,"
            alt={nombreProducto + " " + imagen_.titulo}
          />
        </Col>
      );
    })}
    </>
  )
}

export default ListaImagenes